import {Table, Tag, Image} from 'antd';
import LayoutWithMenu from "@/components/Layouts/LayoutWithMenu";
import {ColumnsType} from "antd/es/table";
import {OrderPlainItemsModel} from '@/types'
import {useRouter} from "next/router";
import {getColorWithStepAndIndex, getDepartmentAndNotesWithStepAndIndex, parseQueryParam} from "@/utils/utils";
import useParameters from "@/hooks/useParameters";
import useOrderPlainItems from "@/hooks/useOrderPlainItems";
import {LeftOutlined} from "@ant-design/icons";
import useRouterUtils from "@/hooks/useRouterUtils";
import {b64Decode} from "@/utils/b64";
import React from "react";


const columns: ColumnsType<OrderPlainItemsModel> = [
    {
        title: "商品编号",
        dataIndex: "goods_no",
    },
    {
        title: "名称",
        dataIndex: "name",
    },
    {
        title: "商品",
        dataIndex: "image",
        width: "80px",
        render: (_, record) => (
            <Image.PreviewGroup>
                <div className='flex flex-row gap-1'>
                    {record.images.map((image_url, index) => (
                        <Image
                            key={`image-${index}`}
                            width={60}
                            height={60}
                            src={image_url}
                            fallback="data:image/png;base64,"/>
                    ))}
                </div>
            </Image.PreviewGroup>
        )
    },
    {
        title: "包装卡片",
        dataIndex: "package_card",
        width: "80px",
        render: (_, record) => (
            <Image
                width={60}
                height={60}
                fallback="data:image/png;base64,"
                src={record.package_card}/>
        )
    },
    {
        title: "sku_no",
        dataIndex: "sku_no",
    },
    {
        title: "颜色",
        dataIndex: "color",
        width: "120px",
    },
    {
        title: "数量",
        dataIndex: "count",
        width: "120px",
    },
    {
        title: "单位",
        dataIndex: "unit",
    },
    {
        title: "单价",
        dataIndex: "unit_price",
    },
    {
        title: "总价",
        dataIndex: "total_price",
    },
    {
        title: "流程进度",
        dataIndex: "step",
        render: (_, record) => (
            <Tag color={getColorWithStepAndIndex(record.current_step, record.current_index)}>
                <div className='text-black'>
                    {getDepartmentAndNotesWithStepAndIndex(record.current_step, record.current_index, record.current_notes)}
                </div>
            </Tag>
        )
    }];


export default function Order() {
    const router = useRouter()
    const { back } = router.query;

    const {page, pageSize} = useParameters()
    let customerNo = parseQueryParam(router.query.customerNo)
    let orderNo = parseQueryParam(router.query.orderNo)
    let backPath = `/goods/order/${customerNo}`
    if (back) {
        backPath = b64Decode(parseQueryParam(back));
    }
    const {orderItems, total, isLoading, isError} = useOrderPlainItems(0, orderNo)
    const {reloadPage}= useRouterUtils();
    return (
        <LayoutWithMenu>
            <div className='m-2 p-5 bg-white rounded'>
                <a
                    className='mr-2'
                    href={backPath}
                >
                    <LeftOutlined/>
                    返回
                </a>
            </div>

            <div className='p-5 m-2 bg-white rounded overflow-auto'>
                <Table
                    bordered={true}
                    size={"small"}
                    className='mx-1'
                    loading={isLoading}
                    columns={columns}
                    pagination={{total: total, current: page, pageSize: pageSize}}
                    dataSource={orderItems}
                    onChange={(pagination, filters, sorter)=> {
                        reloadPage({
                            page: pagination.current,
                            pageSize: pagination.pageSize,
                        })
                    }}
                />
            </div>
        </LayoutWithMenu>
    );
};
