import {Table, Image} from 'antd';
import LayoutWithMenu from "@/components/Layouts/LayoutWithMenu";
import {ColumnsType} from "antd/es/table";
import useParameters from "@/hooks/useParameters";
import useRouterUtils from "@/hooks/useRouterUtils";
import StatROSearchForm from "@/components/stats/returnOrder/StatROSearchForm";
import useReturnOrderStatsByItems from "@/hooks/useReturnOrderStatsByItems";
import {ReturnOrderStatsByItem} from "@/types/stats";
import React from "react";


const columns: ColumnsType<ReturnOrderStatsByItem> = [
    {
        title: "商品编号",
        dataIndex: "goods_no",
        width: "180px",
        sorter: (a, b) => a.sku.goods_no.localeCompare(b.sku.goods_no),
        render: (_, record) => (
            <>
                {record.sku.goods_no}
            </>
        )
    },
    {
        title: "名称",
        dataIndex: "name",
        width: "180px",
        render: (_, record) => (
            <>
                {record.sku.name}
            </>
        )
    },
    {
        title: "商品",
        dataIndex: "image",
        width: "80px",
        render: (_, record) => (
            <Image.PreviewGroup>
                <div className='flex flex-row gap-1'>
                    {record.sku.images.map((image_url, index) => (
                        <Image
                            key={`image-${index}`}
                            width={60}
                            height={60}
                            src={image_url}
                            fallback="data:image/png;base64,"/>
                    ))}
                </div>
            </Image.PreviewGroup>
        )
    },
    {
        title: "包装卡片",
        dataIndex: "package_card",
        width: "80px",
        render: (_, record) => (
            <Image
                width={60}
                height={60}
                fallback="data:image/png;base64,"
                src={record.sku.package_card}/>
        )
    },
    {
        title: "sku_no",
        dataIndex: "sku_no",
        width: "150px",
        render: (_, record) => (
            <>
                {record.sku.sku_no}
            </>
        )
    },
    {
        title: "颜色",
        dataIndex: "color",
        width: "120px",
        render: (_, record) => (
            <>
                {record.sku.color}
            </>
        )

    },
    {
        title: "购买次数",
        dataIndex: "count",
        width: "120px",
        sorter: (a, b) => a.count - b.count,
        render: (_, record) => (
            <>
                {record.count}
            </>
        )
    },
    {
        title: "购买数量",
        dataIndex: "sum",
        width: "120px",
        sorter: (a, b) => a.sum - b.sum,
        render: (_, record) => (
            <>
                {record.sum}
            </>
        )
    },

];


export default function Order() {
    const {page, pageSize} = useParameters()
    const {returnOrderStats, key, isLoading, total} = useReturnOrderStatsByItems()

    const {reloadPage} = useRouterUtils();
    return (
        <LayoutWithMenu>
            <div className='m-2 p-5 bg-white rounded'>
                <StatROSearchForm/>
            </div>

            <div className='p-5 m-2 bg-white rounded overflow-auto'>
                <Table
                    bordered={true}
                    size={"small"}
                    className='mx-1'
                    loading={isLoading}
                    columns={columns}
                    pagination={{total: total, current: page, pageSize: pageSize}}
                    dataSource={returnOrderStats}
                    onChange={(pagination, filters, sorter) => {
                        var obj = {
                            page: pagination.current,
                            pageSize: pagination.pageSize,
                            sorter_field: '',
                            sorter_order: '',
                        }
                        if (!Array.isArray(sorter)) {
                            obj['sorter_field'] = sorter.field as string || ''
                            obj['sorter_order'] = sorter.order as string || ''
                        }
                        reloadPage(obj)
                    }}
                />
            </div>
        </LayoutWithMenu>
    );
};
